{
  "description" : "## Documento de automação remover instância do ELB\n\n---\n### Parâmetros\n* InstanceId\n* ELBGroup",
  "schemaVersion" : "0.3",
  "assumeRole" : "arn:aws:iam::351328429544:role/Cloud-Insert-Remove-EC2-From-ELB",
  "parameters" : {
    "InstanceId" : {
      "type" : "String"
    },
    "ELBGroup" : {
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "name" : "remove",
    "action" : "aws:executeScript",
    "outputs" : [ {
      "Name" : "message",
      "Selector" : "$.Payload.out",
      "Type" : "String"
    } ],
    "inputs" : {
      "Runtime" : "python3.8",
      "Handler" : "script_handler",
      "Script" : "import boto3\r\nimport json\r\n\r\ndef script_handler(event, context):\r\n  elb_client = boto3.client('elb')\r\n  var_familia = event.get('elbgroup')\r\n  var_ec2 = event.get('instanceid')\r\n  out= {}\r\n  response_elb = elb_client.describe_load_balancers()\r\n  for elb in response_elb['LoadBalancerDescriptions']:\r\n    elb_name = elb['LoadBalancerName']\r\n    response_elb_name= elb_client.describe_tags(LoadBalancerNames=[elb_name])\r\n    for tag in response_elb_name['TagDescriptions']:\r\n      elb_name = tag['LoadBalancerName']\r\n      for tagname in tag['Tags']:\r\n        if tagname['Key'] == 'ELBGROUP' and tagname['Value'] == var_familia:\r\n          response_remove_elb = elb_client.deregister_instances_from_load_balancer(LoadBalancerName=elb_name,Instances=[{'InstanceId': var_ec2}])\r\n          out = {\"Response\": response_remove_elb}\r\n          \r\n  return out",
      "InputPayload" : {
        "elbgroup" : "{{ELBGroup}}",
        "instanceid" : "{{InstanceId}}"
      }
    }
  } ]
}
