{
  "schemaVersion": "2.2",
  "description": "Sophos Enterprise antivirus Uninstaller",
  "parameters": {
    "windowsUninstallerURL": {
      "description": "(Required for uninstall of Windows SophosEnterprise) uninstaller url",
      "type": "String",
      "default": "http://mirror.rackspace.com/sophos/Sophos_Uninstaller_GOSS.ps1"
    },
    "workingDirectory": {
      "type": "String",
      "default": "",
      "description": "(Optional) The path where the content will be downloaded and executed from on your instance.",
      "maxChars": 4096
    },
    "executionTimeout": {
      "description": "(Optional) Time in seconds for a command to complete before it is considered to have failed.",
      "type": "String",
      "default": "3600",
      "allowedPattern": "([1-9][0-9]{0,3})|(1[0-9]{1,4})|(2[0-7][0-9]{1,3})|(28[0-7][0-9]{1,2})|(28800)"
    }
  },
  "mainSteps": [
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "action": "aws:runPowerShellScript",
      "name": "UninstallSophosEnterpriseWindows",
      "inputs": {
        "runCommand":["$scriptPath = ((new-object net.webclient).DownloadString('{{windowsUninstallerURL}}')); invoke-command -scriptblock ([scriptblock]::Create($scriptPath))"],
        "workingDirectory": "{{ workingDirectory }}",
        "timeoutSeconds": "{{ executionTimeout }}"
      }
    },
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "action": "aws:runShellScript",
      "name": "UninstallSophosEnterpriseLinux",
      "inputs": {
        "runCommand":["if [ -e /opt/sophos-av/uninstall.sh ]","then","    yes | /opt/sophos-av/uninstall.sh","    echo \"sophos uninstalled\"","else","    echo \"uninstall script does not exist on the VM\"","fi"],
        "workingDirectory": "{{ workingDirectory }}",
        "timeoutSeconds": "{{ executionTimeout }}"
      }
    }
  ]
}
