Content:
{
  "schemaVersion": "2.2",
  "description": "cross-platform np-release",
  "parameters": {
    "commandLinux": {
      "type": "String",
      "description": "Optional. The command to be executed on Linux targets."
    },
    "commandWindows": {
      "type": "String",
      "description": "Optional. The command to be executed on Windows targets."
    }
  },
  "mainSteps": [
    {
      "action": "aws:runPowerShellScript",
      "name": "NP_Release_Windows",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "inputs": {
        "runCommand": [
          "{{ commandWindows }}"
        ]
      }
    },
    {
      "action": "aws:runShellScript",
      "name": "NP_Release_Linux",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "inputs": {
        "runCommand": [
          "{{ commandLinux }}"
        ]
      }
    }
  ]
}
