Content:
{
  "schemaVersion": "2.2",
  "description": "DeviceId used by Sophos to manage the instance",
  "parameters": {
    "workingDirectory": {
      "type": "String",
      "default": "",
      "description": "(Optional) The path where the content will be downloaded and executed from on your instance.",
      "maxChars": 4096
    },
    "executionTimeout": {
      "description": "(Optional) Time in seconds for a command to complete before it is considered to have failed.",
      "type": "String",
      "default": "3600",
      "allowedPattern": "([1-9][0-9]{0,3})|(1[0-9]{1,4})|(2[0-7][0-9]{1,3})|(28[0-7][0-9]{1,2})|(28800)"
    }
  },
  "mainSteps": [
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "action": "aws:runPowerShellScript",
      "name": "GetSophosWindowsDeviceId",
      "inputs": {
        "runCommand":["$filePath = \"C:\\ProgramData\\Sophos\\Management Communications System\\Endpoint\\Persist\\EndpointIdentity.txt\"","if (Test-Path $filePath) {","    try {","        $content = Get-Content -Path $filePath -ErrorAction Stop","        Write-Output $content","    } catch {","        Write-Error \"Error reading file: $filePath. Details: $_\"","        $LASTEXITCODE = 1","    }","} else {","    Write-Error \"File not found: $filePath\"","    $LASTEXITCODE = 1","}","",""],
        "onSuccess": "exit",
        "workingDirectory": "{{ workingDirectory }}",
        "timeoutSeconds": "{{ executionTimeout }}"
      }
    },
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "action": "aws:runShellScript",
      "name": "GetSophosLinuxDeviceId",
      "inputs": {
        "runCommand":["#!/usr/bin/env bash","","file_path=\"/opt/sophos-spl/base/etc/sophosspl/mcs.config\"","","if [ ! -e \"$file_path\" ]; then","    echo \"Error: File not found: $file_path\"","    exit 1","fi","","device_id=$(cat \"$file_path\" | sed -n 's/^device_id=//p')","if [ -z \"$device_id\" ]; then","    echo \"Error: Device ID not found in file: $file_path\"","    exit 1","fi","","echo $device_id",""],
        "onSuccess": "exit",
        "workingDirectory": "{{ workingDirectory }}",
        "timeoutSeconds": "{{ executionTimeout }}"
      }
    }
  ]
}
