Content:
  {
    "description": "This command runs the Command document AWS-RunPatchBaseline on the specified instances.",
    "schemaVersion": "0.3",
    "parameters": {
        "Operation": {
            "type": "String",
            "description": "",
            "default": "Scan"
        },
        "RebootOption": {
            "type": "String",
            "description": "",
            "default": "RebootIfNeeded"
        },
        "SnapshotId": {
            "type": "String",
            "description": "",
            "default": "random_uuid.snapshot_id.result"
        },
        "InstallOverrideList": {
            "type": "String",
            "description": "",
            "default": "(^$)|^https://.+$|^s3://([^/]+)/(.*?([^/]+))$"
        }
    },
    "mainSteps": [
        {
            "name": "runPatchBaseline",
            "action": "aws:runCommand",
            "timeoutSeconds": 7200,
            "maxAttempts": 1,
            "inputs": {
                "DocumentName": "AWS-RunPatchBaseline",
                "Targets": [
                  {
                    "Key": "resource-groups:Name",
                    "Values": [
                      "WebServers"
                    ]
                  }
                ],
                "Parameters": {
                  "Operation": "",
                  "RebootOption": "",
                  "SnapshotId": "",
                  "InstallOverrideList": ""
                },
                "OutputS3BucketName": "patch-manager-execution-logs-use1",
                "OutputS3KeyPrefix": "patching/accountid={{global:ACCOUNT_ID}}/region={{global:REGION}}/executionid={{automation:EXECUTION_ID}}"

            }
        }
    ]
}
